const initialState = {
  eleve: [
    {
      nom: '',
      prenom: '',
      age: 0,
      classe: 0,
    }
  ]
}

function postEleve(state = initialState, action) {
  let nextState = {}
  switch (action.type) {
    case 'POSTER_ELEVE':
      fetch('/', {
        method: 'post',
        headers: { 'Content-Type': 'application/json' },
        body: action.value
      })
      console.log("etat favoris :", state.eleve)
      nextState = {
        ...state,
        eleve: action.value

      }

      return nextState

    default:
      return state
  }
}

export default postEleve
