import React, { useState } from 'react'

const Form = props => {

   const initial = { id: null, Produit: '', Prix: '' }

   const [ user, setUser ] = useState(initial)

         const changed = event => {

            const { name, value } = event.target

            setUser({ ...user, [name]: value })
      }

  var openFile = function(event) {
    var input = event.target;

    var file = input;
    console.log(file);
  };


	return (
		<form id="test"
            onSubmit={event => {
               event.preventDefault()
               if (!user.Produit || !user.Prix) return

               props.ajouter(user)
               props.setCount(props.count +1)

               setUser(initial)
            }}>
               <label id="nom">Produit</label>
               <input type="text" name="Produit" value={user.Produit} onChange={changed}/><br/>
               <label>Prix</label>
               <input type="number" name="Prix"  value={user.Prix} onChange={changed}/><br/>
               <input type='file' onchange='openFile(event)'></input><br/>
               <button id="ajout" className="btn btn-info">Ajouter</button>



		</form>
   )
}
export default Form
