import React, { useState } from 'react';

import Form from './Component/forme';
import Tableau from './Component/tableau';
import './App.css';
import './Component/forme.css';
import  './Component/tableau.css';

const App = () => {
    const usersData = []

    const [ users, setUsers ] = useState(usersData)

    const [count, setCount] = useState(0);

    const ajouter = user => {
      user.id = count
      setUsers([ ...users, user ])
    }

    const deleteUser = id => {
        setUsers(users.filter(user => user.id !== id))
    }

    return (
      <div id="corps" className="container">
        <div className="flex-row">
            <div className="flex-large">

              <Form ajouter={ajouter} count={count} setCount={setCount} />

            </div>
            <div className="flex-large">
              <Tableau users={users} deleteUser={deleteUser}/>
            </div>

        </div>

    </div>
    )
  };


export default App;
