import React, { useState } from 'react'
const Form = props => {
   const initial = { id: null, Produits: '', Prix: '' }
   const [ user, setUser ] = useState(initial)
   const changed = event => {
   const { name, value } = event.target
   setUser({ ...user, [name]: value })




  var openFile = function(event) {
    var input = event.target;

    var file = input.files[0];
    console.log(file.lastModifiedDate);
  };


 }
	return (
		<form id="test" className="row"
      onSubmit={event => {
         event.preventDefault()

         if (!user.Produits || !user.Prix) return

         if(isNaN(user.Prix)){
            var erreur = document.getElementById('erreur')
            erreur.innerHTML = 'Entrer un nombre'

        }else{
            var erreur = document.getElementById('erreur')
            erreur.innerHTML = ''
            //props.updateUser(user.id, user)
            props.ajouter(user)
            props.setCount(props.count + 1)
            setUser(initial)
        }


       }}>
         <div id="id" className="flex-row">
			<label>Produit</label>
			<input type="text" name="Produits" className="id1" value={user.Produits} onChange={changed}/><br></br>
			<label id="prix">Prix</label>
			<input type="text" name="Prix" className="id2"  value={user.Prix} onChange={changed} /><br></br>
         <div class="form-group">
         <label id= "for">Description</label>
         <textarea class="form-control rounded-0" id="exampl" rows="3"></textarea>
         </div>
         <input type='file' id="file" onchange='openFile(event)'></input>
			<button id="ajout" className="btn btn-primary">Ajouter</button>
         <p id="erreur"></p>
         </div>


		</form>
   )
}
 export default Form
