import React, { useState } from 'react'

const Form = props => {

   const initial = { id: null, Produit: '', Prix: '' }

   const [ user, setUser ] = useState(initial)

         const changed = event => {

            const { name, value } = event.target

            setUser({ ...user, [name]: value })
      }

	return (
		<form id="test"
            onSubmit={event => {
               event.preventDefault()
               if (!user.Produit || !user.Prix) return

               props.ajouter(user)
               props.setCount(props.count +1)

               setUser(initial)
            }}>

               <label id="nom">Produit</label>
               <input type="text" name="nom" value={user.Produit} onChange={changed}/><br/>
               <label>Prix</label>
               <input type="text" name="prenom"  value={user.Prix} onChange={changed}/><br/>

               <form className="md-form" action="#">
               <button type="file" multiple>File</button>
               </form>

               <button id="ajout" className="btn btn-info">Ajouter</button>

		</form>

   )
}
export default Form
